def number_account(a)
    puts "Il y a : #{a.count} comptes twitter"
end

def shortest_account(a)
    shortest = a.sort_by(&:length)
    puts "Le nom de compte le plus court est #{shortest[0]}"
end

def number_5_caracter(a)
    names = a.count {|x| x.length == 6}
    puts "il y #{names} comptes qui ont 5 carrectères dans leurs pseudos"
end

def start_maj(a)
    account_start_maj = a.count {|x| x[1].match?(/[A-Z]/)}
    puts "Dans les comptes twitter il y a #{account_start_maj} compte qui commence avec une majuscule"

end

def list_a_to_z(a)
    list = a.sort
    puts list
end

def list_by_size(a)
    list_size = a.sort_by(&:length)
    puts list_size
end

def position_account(a)
    position = a.index("@epenser")
    puts "@epenser est a la #{position} places"
end

def handle_size(a)
    trois = a.count {|a| a.length == 4}
    quatre = a.count {|a| a.length == 5}
    cinq= a.count {|a| a.length == 6}
    six = a.count {|a| a.length == 7}
    sept = a.count {|a| a.length == 8}
    huit = a.count {|a| a.length == 9}
    neuf = a.count {|a| a.length == 10}
    dix = a.count {|a| a.length == 11}
    onze = a.count {|a| a.length == 12}
    douze = a.count {|a| a.length == 13}
    treize = a.count {|a| a.length == 14}
    quatorze= a.count {|a| a.length == 15}
    quinze = a.count {|a| a.length == 16}
    seize = a.count {|a| a.length > 17}


    puts "il y a #{trois} nom de compte avec 3 carrectères"
    puts "il y a #{quatre} noms de compte avec 4 carrectères"
    puts "il y a #{cinq} noms de compte avec 5 carrectères"
    puts "il y a #{six} noms de compte avec 6 carrectères"
    puts "il y a #{sept} noms de compte avec 7 carrectères"
    puts "il y a #{huit} noms de compte avec 8 carrectères"
    puts "il y a #{neuf} noms de compte avec 9 carrectères"
    puts "il y a #{dix} noms de compte avec 10 carrectères"
    puts "il y a #{onze} noms de compte avec 11 carrectères"
    puts "il y a #{douze} noms de compte avec 12 carrectères"
    puts "il y a #{treize} noms de compte avec 13 carrectères"
    puts "il y a #{quatorze} noms de compte avec 14 carrectères"
    puts "il y a #{quinze} noms de compte avec 15 carrectères"
    puts "il y a #{seize} nom de compte avec 32 carrectères"
end

